PRODUCT_NUMBER NUMBER CONSTRAINT PK_PRODUCT PRIMARY KEY,
MEMBER_NUMBER NUMBER,
PRODUCT_NAME VARCHAR2(100),
PRODUCT_CATEGORY VARCHAR2(100),
PRODUCT_PRICE NUMBER,
PRODUCT_STOCK NUMBER,
PRODUCT_MANUFACTURE_DATE DATE,
PRODUCT_EXPIRATION_DATE DATE,
PRODUCT_ONELINE_INFORMATION VARCHAR2(1000),
PRODUCT_INFORMATION VARCHAR2(1000),
PRODUCT_THUMBNAIL_NAME VARCHAR2(100),
PRODUCT_MAIN_IMAGE_NAME VARCHAR2(100),
PRODUCT_REGIST_DATE DATE,
PRODUCT_MODIFY_DATE DATE,
PRODUCT_VIEW_COUNT NUMBER,
PRODUCT_TODAY_SALE DATE,
PRODUCT_OPEN_HOURS DATE,
PRODUCT_SALE_PERCENT NUMBER,
PRODUCT_SALE_PRICE NUMBER,
CONSTRAINT FK_PRODUCT FOREIGN KEY (MEMBER_NUMBER) REFERENCES
TBL_MEMBER (MEMBER_NUMBER)
);
SELECT * FROM (
SELECT /*INDEX_DESC (TBL_PRODUCT PK_PRODUCT) */ ROWNUM RN, PRODUCT_NUMBER, PRODUCT_THUMBNAIL_NAME, PRODUCT_PRICE, PRODUCT_INFORMATION
FROM TBL_PRODUCT
WHERE PRODUCT_NUMBER > 0 AND ROWNUM >0 AND ROWNUM <=20
)
WHERE RN > 10

SELECT PRODUCT_NUMBER, PRODUCT_THUMBNAIL_NAME,PRODUCT_PRICE, PRODUCT_INFORMATION
FROM
(SELECT ROWNUM R, D2.*
FROM
(
	SELECT P.* FROM TBL_PRODUCT  P
	ORDER BY PRODUCT_NUMBER DESC
) D2
	WHERE ROWNUM <=20
	)WHERE R > 10;

