mapper.update:
UPDATE TBL_PARTNER
SET PARTNER_STORE_NAME = #{partnerStoreName}, PARTNER_REGISTRATION_NUMBER = #{partnerRegistrationNumber}, PARTNER_OWNER_NAME = #{partnerOwnerName}, PARTNER_OWNER_EMAIL = #{partnerOwnerEmail}, PARTNER_STORE_ADDRESS = #{partnerStoreAddress}, PARTNER_OWNER_PHONE_NUMBER = #{partnerOwnerPhoneNumber}
WHERE PARTNER_NUMBER = #{partnerNumber}
