mapper.select:
		SELECT REVIEW_NUMBER, PRODUCT_NUMBER, REVIEW_CONTENT, REVIEW_REGIST_DATE
        FROM
        (
            SELECT ROWNUM R, REVIEW_NUMBER, PRODUCT_NUMBER, REVIEW_CONTENT, REVIEW_REGIST_DATE
            FROM
            (
                SELECT REVIEW_NUMBER, PRODUCT_NUMBER, REVIEW_CONTENT, REVIEW_REGIST_DATE
                FROM TBL_REVIEW
                WHERE PRODUCT_NUMBER = #{productNumber}
                ORDER BY REVIEW_REGIST_DATE DESC
            )D2

        WHERE ROWNUM <= #{page} * #{amount}
        ) WHERE R > (#{page} - 1) * #{amount}

    SELECT COUNT(*)
	FROM TBL_REVIEW
	where PRODUCT_NUMBER = #{productNumber}
